import React from "react";
import Meme2 from "./Meme2";
// import MemeResult from "./MemeResult";


export default class Meme1 extends React.Component {
  constructor() {
    super();
    this.state = {
      ids: "",
      username: "shivangi24",
      password: "shivangi@24",
      topText: "",
      bottomText: "",
      url: ""
    };
  }

  handleChange = (event) => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };

  afterButtonClick = () => {
    const data = {
      template_id: this.state.ids,
      username: this.state.username,
      password: this.state.password,
      text0: this.state.topText,
      text1: this.state.bottomText
    };

    fetch("https://api.imgflip.com/caption_image?", {
      method: "POST",
      body: new URLSearchParams(data)
    })
      .then((res) => res.json())
      .then((json) => {
        this.setState({
          url: json.data.url
        });
        console.log(this.state.url);
      });
  };
  render() {
    return (
      <div>
        <form>
          <h1>Enter your details:</h1>

          <input
            type="text"
            id="ids"
            name="ids"
            placeholder="Enter Template_Id"
            onChange={this.handleChange}
          />
          <br />

          <input
            type="text"
            id="topText"
            name="topText"
            placeholder="Enter topText"
            onChange={this.handleChange}
          />
          <br />

          <input
            type="text"
            id="bottomText"
            name="bottomText"
            placeholder="Enter bottomText"
            onChange={this.handleChange}
          />
          <br />

          <button type="button" onClick={this.afterButtonClick}>
            Click Now
          </button>
        </form>
        <Meme2 url={this.state.url} />
      </div>
    );
  }
}
