import React from 'react';
import { Link } from 'react-router-dom';
import './App.css';
import Meme1 from './Component/Meme1';
class App extends React.Component {
    // Constructor
    constructor()
    {
        super()
        this.state = {
            items:[],
            isLoaded:false
        }
    }

    componentDidMount(){
        fetch("https://api.imgflip.com/get_memes").then((res) => res.json()).then((json) => {
            this.setState({
                items:json.data.memes,
                isLoaded:true
            })
        });
    }

    render(){
        const {isLoaded, items} = this.state;
        if(isLoaded)
        {
            console.log(items);
            return (
                <div>
                    <Link to="/meme">Click1</Link>
                    <button href="./Component/Meme.js" className="re">click here</button>
                    <h1>MEMES</h1>

                    {
                        items.map((item)=>(

                          <Link to="/meme" ><Meme1  key={item.id} src={item.url} alt={item.name} /><img  key={item.id} src={item.url} alt={item.name} style={{height:200+'px',width:200+'px',margin:20+'px'}}/></Link>

                        ))
                    }
                </div>);
        }
        else
            return <h1>Loading...</h1>
    }
}
export default App;
